+  /* ê·¸ë¼ë°ì´ì…˜ */
+  --gradient-primary: linear-gradient(135deg, var(--primary-color) 0%, var(--secondary-color) 100%);
+  --gradient-light: linear-gradient(135deg, var(--primary-light) 0%, var(--secondary-light) 100%);
+  
+  /* ì•¡ì„¼íŠ¸ ìƒ‰ìƒ */
+  --accent-warm: #ffd89b;
